    private void cbPaket1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbPaket1ActionPerformed
        int nasiPutih, nasiGoreng, nasiCapcay;
        int paket = cbPaket1.getSelectedIndex();
        switch(paket){
            case 0:
                break;
            case 1:
                txtPaket1.setText("Paket 1");
                txtHarga1.setText(String.valueOf(5000));
                break;
            case 2:
                txtPaket1.setText("Paket 1");
                txtHarga1.setText(String.valueOf(12000));
                break;
            case 3:
                txtPaket1.setText("Paket 1");
                txtHarga1.setText(String.valueOf(15000)); 
                break;
        }
    }//GEN-LAST:event_cbPaket1ActionPerformed

