    private void btnBayarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBayarActionPerformed
        // Mengambil Input Jumlah Pembelian
        int jumlahPaket1 = Integer.parseInt(txtJml1.getText());
        int jumlahPaket2 = Integer.parseInt(txtJml2.getText());
        int jumlahPaket3 = Integer.parseInt(txtJml3.getText());
        
        // Mengambil Input Harga Paket
        int hargaPaket1 = Integer.parseInt(txtHarga1.getText());
        int hargaPaket2 = Integer.parseInt(txtHarga2.getText());
        int hargaPaket3 = Integer.parseInt(txtHarga3.getText());
        
        // Membuat Variabel Bayar Tiap Paket
        int bayarPaket1 = jumlahPaket1*hargaPaket1;
        int bayarPaket2 = jumlahPaket2*hargaPaket2;
        int bayarPaket3 = jumlahPaket3*hargaPaket3;
        
        // Set Text Bayar
        txtBayar1.setText(String.valueOf("Rp."+bayarPaket1));
        txtBayar2.setText(String.valueOf("Rp."+bayarPaket2));
        txtBayar3.setText(String.valueOf("Rp."+bayarPaket3));
        
        // Membuat Variabel Total Pesanan dan Set ke Text Total
        int total = bayarPaket1+bayarPaket2+bayarPaket3;
        txtTotal.setText(String.valueOf("Rp."+total));
        
    }//GEN-LAST:event_btnBayarActionPerformed

